export default {
	enviar: async (nome, email, msg) =>{
		let contato={
			nome,email,msg
		};

		let requisicao = {
			method : "POST",
			headers: {'Content-Type': 'application/json'},
			body: JSON.stringify(contato)
		};

		const response = await fetch('http://localhost:8080/projetos/php/');
		const data = await response.json();

		return data.msg;
	}
}
