export default {
	enviar: async (nome, email, msg) =>{
		let contato={
			nome,email,msg}

		fetch("http://localhost:8080/projetos/php/contato.php",{
			method: "POST",
			mode: "cors",
			headers: {'Content-Type' : 'appliction/json'},
			body: JSON.stringify(contato)
		}).then(resp => resp.json()).then((data) => {
			return data;
		}).catch(err => console.log(err))

	}
}
